"use client";

import React, { forwardRef, useRef } from "react";

import { AnimatedBeam } from "@/components/magicui/animated-beam";
import { cn } from "@/lib/utils";

const Circle = forwardRef<
  HTMLDivElement,
  { className?: string; children?: React.ReactNode }
>(({ className, children }, ref) => {
  return (
    <div
      ref={ref}
      className={cn(
        "z-10 flex size-12 items-center justify-center rounded-full border-2 border-border bg-white p-3 shadow-[0_0_20px_-12px_rgba(0,0,0,0.8)]",
        className
      )}
    >
      {children}
    </div>
  );
});

Circle.displayName = "Circle";

export function CourriersBeam({ className }: { className?: string }) {
  const containerRef = useRef<HTMLDivElement>(null);
  // Left side carrier circles
  const carrier1Ref = useRef<HTMLDivElement>(null);
  const carrier2Ref = useRef<HTMLDivElement>(null);
  const carrier3Ref = useRef<HTMLDivElement>(null);
  const carrier4Ref = useRef<HTMLDivElement>(null);
  const carrier5Ref = useRef<HTMLDivElement>(null);
  // Center logo
  const logoRef = useRef<HTMLDivElement>(null);
  // Right side carrier circles
  const carrier6Ref = useRef<HTMLDivElement>(null);
  const carrier7Ref = useRef<HTMLDivElement>(null);
  const carrier8Ref = useRef<HTMLDivElement>(null);
  const carrier9Ref = useRef<HTMLDivElement>(null);
  const carrier10Ref = useRef<HTMLDivElement>(null);
  // User
  const userRef = useRef<HTMLDivElement>(null);

  return (
    <div
      className={cn(
        "relative flex h-[500px] w-full items-center justify-center overflow-hidden p-10",
        className
      )}
      ref={containerRef}
    >
      <div className="flex size-full max-w-6xl flex-row items-stretch justify-between gap-10">
        {/* Left carriers */}
        <div className="flex flex-col justify-center gap-2">
          <Circle ref={carrier1Ref}>
            <Icons.dnclexpress />
          </Circle>
          <Circle ref={carrier2Ref}>
            <Icons.ups />
          </Circle>
          <Circle ref={carrier3Ref}>
            <Icons.fedexground />
          </Circle>
          <Circle ref={carrier4Ref}>
            <Icons.usps />
          </Circle>
          <Circle ref={carrier5Ref}>
            <Icons.fedexexpress />
          </Circle>
        </div>

        {/* Center logo */}
        <div className="flex flex-col justify-center">
          <Circle ref={logoRef} className="size-20">
            <Icons.yywireless />
          </Circle>
        </div>

        {/* Right carriers */}
        <div className="flex flex-col justify-center gap-2">
          <Circle ref={carrier6Ref}>
            <Icons.dnclexpress />
          </Circle>
          <Circle ref={carrier7Ref}>
            <Icons.ups />
          </Circle>
          <Circle ref={carrier8Ref}>
            <Icons.fedexground />
          </Circle>
          <Circle ref={carrier9Ref}>
            <Icons.usps />
          </Circle>
          <Circle ref={carrier10Ref}>
            <Icons.fedexexpress />
          </Circle>
        </div>

        {/* User */}
        <div className="flex flex-col justify-center">
          <Circle className="size-16" ref={userRef}>
            <Icons.user />
          </Circle>
        </div>
      </div>

      {/* Left carriers to center logo beams */}
      <AnimatedBeam
        containerRef={containerRef}
        fromRef={carrier1Ref}
        toRef={logoRef}
        duration={3}
      />
      <AnimatedBeam
        containerRef={containerRef}
        fromRef={carrier2Ref}
        toRef={logoRef}
        duration={3}
      />
      <AnimatedBeam
        containerRef={containerRef}
        fromRef={carrier3Ref}
        toRef={logoRef}
        duration={3}
      />
      <AnimatedBeam
        containerRef={containerRef}
        fromRef={carrier4Ref}
        toRef={logoRef}
        duration={3}
      />
      <AnimatedBeam
        containerRef={containerRef}
        fromRef={carrier5Ref}
        toRef={logoRef}
        duration={3}
      />

      {/* Center logo to right carriers beams */}
      <AnimatedBeam
        containerRef={containerRef}
        fromRef={logoRef}
        toRef={carrier6Ref}
        duration={3}
      />
      <AnimatedBeam
        containerRef={containerRef}
        fromRef={logoRef}
        toRef={carrier7Ref}
        duration={3}
      />
      <AnimatedBeam
        containerRef={containerRef}
        fromRef={logoRef}
        toRef={carrier8Ref}
        duration={3}
      />
      <AnimatedBeam
        containerRef={containerRef}
        fromRef={logoRef}
        toRef={carrier9Ref}
        duration={3}
      />
      <AnimatedBeam
        containerRef={containerRef}
        fromRef={logoRef}
        toRef={carrier10Ref}
        duration={3}
      />

      {/* Right carriers to user beams */}
      <AnimatedBeam
        containerRef={containerRef}
        fromRef={carrier6Ref}
        toRef={userRef}
        duration={3}
      />
      <AnimatedBeam
        containerRef={containerRef}
        fromRef={carrier7Ref}
        toRef={userRef}
        duration={3}
      />
      <AnimatedBeam
        containerRef={containerRef}
        fromRef={carrier8Ref}
        toRef={userRef}
        duration={3}
      />
      <AnimatedBeam
        containerRef={containerRef}
        fromRef={carrier9Ref}
        toRef={userRef}
        duration={3}
      />
      <AnimatedBeam
        containerRef={containerRef}
        fromRef={carrier10Ref}
        toRef={userRef}
        duration={3}
      />
    </div>
  );
}
